mapper.insert:

  SELECT upload_seq.nextval FROM dual

INSERT INTO upload_t (
    upload_no
  , uploader
  , ip
  , upload_dt
) VALUES (
   #{uploadNo}
  , #{uploader}
  , #{ip}
  , CURRENT_DATE
)

INSERT INTO file_t (
     file_no
   , upload_path
   , original_filename
   , filesystem_name
   , upload_no
 ) VALUES (
     file_seq.nextval
   , #{uploadPath}
   , #{originalFilename}
   , #{filesystemName}
   , #{uploadNo}
 )

mapper.select:
SELECT upload_no, uploader, ip, upload_dt
  FROM upload_t
 WHERE upload_no = #{uploadNo}

SELECT file_no, upload_path, original_filename, filesystem_name
  FROM file_t
 WHERE upload_no = #{uploadNo}
